mapper.select:
	    SELECT a.lxname, a.lxlink, a.lxfile, b.coxname, a.lxtype, a.lxnum
	    FROM lecture a
	    	LEFT OUTER JOIN course b
			ON a.coxseq = b.coxseq
  		WHERE a.coxseq = #{coxseq}
  		AND a.lxseq=#{lxseq}

mapper.update:
	UPDATE lecture
	SET regdate = regdate 
	
		,lxname=#{lxname}
	
    	,lxlink=#{lxlink}
    
    
    	,lxfile=#{lxfile}
    
	WHERE coxseq=#{coxseq}
		AND lxseq=#{lxseq}

UPDATE lecture
SET lxfile=#{fileName}
WHERE coxseq=#{coxseq}
	AND lxseq=#{lxseq}

mapper.delete:
DELETE FROM lecture
WHERE coxseq=#{coxseq}
	AND lxseq=#{lxseq}
