mapper.select:
		select cxseq, cxgubun, cxcode, cxname
		from code
		where cxseq is not null
		
         
             and cxgubun = '000'
         
         
             
		    	and cxgubun = #{cxgubun}
		     
         
	    

		
	    	and cxseq = #{cxseq}
	    
		
	    	and cxname = #{cxname}
	    
		order by cxcode, cxname

select a.coxseq, a.coxcate, a.coxname, a.coxstart, a.coxend, a.coxreqstart, a.coxreqend, a.tuxseq, a.coxintro, a.coxopen, a.coxprog, a.coxscore, a.coxoffice, a.regdate, b.cxname from course a, code b where a.coxcate = b.cxseq
and a.coxoffice = #{coxoffice}
and a.coxseq = #{coxseq}
and date_format(now(), '%Y%m%d') between a.coxstart and a.coxend
and a.coxname like CONCAT('%', #{coxname}, '%')
order by a.coxstart desc, a.coxseq desc

select a.tuxseq, b.mxname from tutor a, member b where a.mxseq = b.mxseq
and a.tuxseq = #{tuxseq}
order by 2

select a.txseq, a.coxseq, a.lxseq, a.txtype, a.txcont, b.lxnum, b.lxname, a.txcont, a.tx1, a.tx2, a.tx3, a.tx4, a.tx5, a.txcorrect, c.cxname, d.coxname from testpool a, lecture b, code c, course d where a.coxseq is not null and a.lxseq = b.lxseq and a.txtype = c.cxcode and c.cxgubun = '004' and a.coxseq = d.coxseq
and a.txseq = #{txseq}
and a.coxseq = #{coxseq}
order by b.lxnum, a.txseq

select lxseq, lxnum, coxseq, lxname, lxtype, lxlink, lxfile, regdate, mxseq from lecture where coxseq is not null
and lxseq = #{lxseq}
and coxseq = #{coxseq}
order by coxseq, lxnum

select a.tpxseq, a.tpxtitle, a.tpxfrom, a.tpxto, a.tpxduring, a.tpx1cnt, a.tpx2cnt, a.tpx3cnt, a.tpx1score, a.tpx2score, a.tpx3score, a.regdate, a.coxseq, b.coxname from testpaper a, course b where a.coxseq = b.coxseq
and a.tpxseq = #{tpxseq}
order by a.tpxto desc, a.tpxfrom desc
