mapper.select:
SELECT * 
FROM lexstudylog a, lecture b
WHERE a.mxseq = #{USEQ}
	AND a.lxseq = b.lxseq
	AND b.coxseq = #{coxseq}

mapper.delete:
DELETE FROM apply
WHERE coxseq=#{coxseq}
AND mxseq=#{mxseq}

mapper.select:
		SELECT count(a.regdate) AS cnt
		FROM lexstudy a
			LEFT OUTER JOIN lecture b
			ON a.lxseq = b.lxseq
				AND a.mxseq = #{USEQ}
    	WHERE b.coxseq=#{coxseq} AND a.mxseq=#{USEQ}

	SELECT regdate
    FROM lexstudy
    WHERE mxseq = #{USEQ} and lxseq=#{lxseq}

mapper.update:
UPDATE lexstudy
SET regdate=DATE_FORMAT(NOW(), '%Y%m%d')
WHERE mxseq = #{USEQ} and lxseq=#{lxseq}
